import json
from quart import Quart, render_template, request

app = Quart(__name__)


FLAGS = [
    "this_is_not_an_exit",  # door
    "we_re_not_strangers_to_love",  # lock
    "crunching_is_bad_for_your_health",  # key

    "",  # nonce
    "",  # k
    "",  # 3021from2759
]


@app.route("/")
async def index():
    return await render_template("index.html")


@app.route("/check-flag", methods=["POST"])
async def check_flag():
    flag = (await request.form).get("flag")
    return await render_template("index.html", flag_status="ok" if flag in FLAGS else "wrong")


if __name__ == "__main__":
    app.run("0.0.0.0")
